		function prepareSelectsForEloqua(elqForm) {
		   var selects = elqForm.getElementsByTagName("SELECT");
		   for (var i = 0; i < selects.length; i++) {
		       if (selects[i].multiple) {
		           createEloquaSelectField(elqForm, selects[i]);
