		function submitForm(elqForm) {
		   if (CheckElqForm(elqForm)) {
		       prepareSelectsForEloqua(elqForm);
		       fnPrepareCheckboxMatricesForEloqua(elqForm);
		       return true;
		   }
		   else { return false; }
